interface Response {
    /**
     * @see https://tosspayments.slack.com/archives/C01APUDQ8CF/p1619703053383200?thread_ts=1619703046.383100&cid=C01APUDQ8CF
     * @description
     * NONE - 정상적인 Customer Session 이 존재하지 않음
     * INVALID - DB에 존재하지 않는 고객 (약관 미동의)
     * PENDING - access token 받아가기를 기다리는 중
     * APPROVED - 유효한 customer token이 응답됨
     * EXPIRED - PENDING 상태 진입 이후 10초 경과, 혹은 APPROVED 이후 만료
     * FAILED - access token 요청중 오류발생한 경우
     */
    status: 'NONE' | 'PENDING' | 'EXPIRED' | 'APPROVED' | 'INVALID' | 'FAILED';
    customerToken?: string;
    expiresIn?: number;
}
interface ApprovedResponse extends Response {
    status: 'APPROVED';
    customerToken: string;
    expiresIn: number;
}
interface OtherResponse extends Response {
    status: 'NONE' | 'PENDING' | 'EXPIRED' | 'INVALID' | 'FAILED';
    customerToken?: never;
    expiresIn?: never;
}
export default function getCustomerToken(clientKey: string, customerKey: string): Promise<ApprovedResponse | OtherResponse>;
export {};
